data.script:
return {
    command: "query",
    payload: {
        'data.email': input.inputData.user.email,
        documentTypeId: "674af84b390db671217ceab6",
        "$limit": 1
    }
}

const doc = input.previousCommandOutput[0]

const inputDocument = input.inputDocument;
const documentData = inputDocument.data;

const projektModularioVyvoj = documentData.ulohaModularioVyvoj?.projekt;
const ulohaVyvojNaMieru = documentData.ulohaVyvojNaMieru?.projekt;
const ulohaDigitalizaciaCezModulario = documentData.ulohaDigitalizaciaCezModulario?.projekt;

// If at least one project is filled, use it as project relation. Otherwise keep project empty
const project =
  projektModularioVyvoj ||
  ulohaVyvojNaMieru ||
  ulohaDigitalizaciaCezModulario;

log("project", project);

// Build the base update object
const updateData = {
  "data.kolega._uid": doc._id,
  "data.kolega.email": doc.data.email,
  "data.kolega.menoAPriezvisko": doc.data.menoAPriezvisko
};

// If a project exists, include its fields
if (project) {
  updateData["data.projekt._uid"] = project._uid;
  updateData["data.projekt._name"] = project._name;
  updateData["data.projekt.menoAPriezvisko"] = project.menoAPriezvisko;
}

return {
  command: "updateOne",
  payload: {
    filter: {
      _id: input.inputDocument._id,
    },
    update: updateData
  },
};

const { kolega } = input.inputDocument.data

return {
  command: "query",
  payload: {
    documentTypeId: "6773ff42a1b020afb199673b", // Kolegovia - hodinovky
    "data.kolega.email": kolega?.email ?? ''
  },
};

const {koeficient = "" } = input.inputDocument.data

return {
  command: "query",
  payload: {
    documentTypeId: "67c1964fcbeffdd9825ee20d", // Trackovanie koeficient
    "data._name": koeficient?._name ?? ' '
  },
  output: { ress: input.previousCommandOutput }
};

const doc = input.inputDocument
const docData = doc.data
const koeficient = input.previousCommandOutput[0]?.data?.koeficient
const hoursRates = input.previousScriptOutput?.ress?.map(o => o.data)
const projektHoursRate = hoursRates.filter( o => o?.projekt?._name === docData?.projekt?._name)

const onlyProjectRates = (o) => o?.projekt?._name === docData?.projekt?._name || !o?.projekt?._name

const getLastApprovedRate = (rates) => rates
        .sort((a, b) => a.do - b.do )
        .filter( o => docData?.zaciatok < o?.do && onlyProjectRates(o)  )
        .pop();

const getLastCurrentRate = (rates) => rates
        .filter(o => !o?.do && onlyProjectRates(o))
        .sort((a, b) => a.hodinovka - b.hodinovka)
        .pop();

const lastApprovedRate =
    getLastApprovedRate(
      projektHoursRate?.length ? projektHoursRate : hoursRates
    ) ||
    getLastCurrentRate(
      projektHoursRate?.length ? projektHoursRate : hoursRates
    );

const hoursRate = lastApprovedRate?.hodinovka;
const finallHoursRate = koeficient ? hoursRate * koeficient : hoursRate;
const sumColleague = Math.round( ( (finallHoursRate ?? 0) * docData?.trvanie ) * 100 ) / 100

return {
  command: "updateOne",
  payload: {
    filter: {
      _id: doc._id,
    },
    update: {
      "data.hodinovkaKolega": finallHoursRate,
      "data.sumaKolega": sumColleague,
      "data.sumaKolegaVEur": Math.round((sumColleague / ( lastApprovedRate?.kurz ?? docData?.hodinovkaKolegaKurz )) * 100) / 100 || sumColleague,
      "data.hodinovkaKolegaKurz": lastApprovedRate?.kurz ?? docData?.kurz
    }, 
  },
};

const docData = input.inputDocument.data

return {
  command: "query",
  payload: {
    documentTypeId: "67518228872c0cd4254de11b", // Projekty - hodinovky
    $or: [
      { "data.kolega.email": docData?.kolega.email },
      { "data.projekt._name": docData?.projekt?._name }
    ]
  },
};

const doc = input.inputDocument
const docData = doc.data
const hoursRates = input.previousCommandOutput.map(o => o.data)
const projektHoursRate = hoursRates.filter( o => o?.projekt?._name === docData?.projekt?._name)

const onlyProjectRates = (o) => o?.projekt?._name === docData?.projekt?._name || !o?.projekt?._name

const getLastApprovedRate = (rates) => rates
        .sort((a, b) => a.do - b.do )
        .filter( o => docData?.zaciatok < o?.do && onlyProjectRates(o))
        .pop()

const getLastCurrentRate = (rates) => rates
        .filter( o => !o?.do && onlyProjectRates(o))
        .sort((a, b) => a.hodinovaSadzba - b.hodinovaSadzba )
        .pop()
        
const finallHoursRate = getLastApprovedRate((projektHoursRate?.length ? projektHoursRate : hoursRates)) || getLastCurrentRate((projektHoursRate?.length ? projektHoursRate : hoursRates))
const sumClient = Math.round( ( (finallHoursRate?.hodinovaSadzba ?? 0) * docData?.trvanie ) * 100 ) / 100


return {
  command: "updateOne",
  payload: {
    filter: {
      _id: doc._id,
    },
    update: {
      "data.hodinovkaKlient": finallHoursRate?.hodinovaSadzba,
      "data.sumaKlient": sumClient,
      "data.sumaKlientVEur": Math.round((sumClient / ( finallHoursRate?.kurz ?? docData?.hodinovkaKlientKurz )) * 100) / 100 || sumClient,
      "data.hodinovkaKlientKurz": finallHoursRate?.kurz ?? docData?.kurz
    },
  },
};

const rec = input.inputDocument;

return {
  command: "query",
  payload: {
    documentTypeId: '67517022872c0cd4254dd9ae',
    'data._name': rec.data.projekt._name,
    _id: rec.data.projekt._uid
  },
};

const rec = input.inputDocument
const projects = input.previousCommandOutput

return {
    command: "query",
    payload: {
        documentTypeId: '674af84b390db671217ceab6',
        _id: rec.data.kolega._uid
    },
    output: {
        projects,
    }
};

const rec = input.inputDocument.data;
const user = input.previousCommandOutput[0].data;
const project = input.previousScriptOutput.projects[0].data;

log(user)
if (rec.koeficient && rec.koeficient._name === 'Vzdelávanie') {
  return {
    command: "updateOne",
    payload: {
      filter: {
        _id: input.inputDocument._id,
      },
      update: {
        "data.vykazSchvaluje": {
          _uid: '674af84b390db671217ceab9',
          _name: 'Milan Cák',
          email: 'admin@amcef.com'
        }
      },
    }
  };
}
if (rec.projekt._name === 'AMCEF') {
  if (!user.nadriadeny || !user.nadriadeny.email) {
    return {
      command: "updateOne",
      payload: {
        filter: {
          _id: input.inputDocument._id,
        },
        update: {
          "data.vykazSchvaluje": {
            _uid: '674af84b390db671217ceab9',
            _name: 'Milan Cák',
            email: 'admin@amcef.com'
          }
        },
      }
    };
  } else {
    return {
      command: "updateOne",
      payload: {
        filter: {
          _id: input.inputDocument._id,
        },
        update: {
          "data.vykazSchvaluje": user.nadriadeny
        },
      }
    };
  }
} else {
  if (rec.kolega.email !== project.primarnyManazer?.email && project.primarnyManazer) {
    return {
      command: "updateOne",
      payload: {
        filter: {
          _id: input.inputDocument._id,
        },
        update: {
          "data.vykazSchvaluje": project.primarnyManazer
        },
      }
    };
  } else {
    if (!user.nadriadeny || !user.nadriadeny.email) {
      return {
        command: "updateOne",
        payload: {
          filter: {
            _id: input.inputDocument._id,
          },
          update: {
            "data.vykazSchvaluje": {
              _uid: '674af84b390db671217ceab9',
              _name: 'Milan Cák',
              email: 'admin@amcef.com'
            }
          },
        }
      };
    } else {
      return {
        command: "updateOne",
        payload: {
          filter: { _id: input.inputDocument._id },
          update: {
            "data.vykazSchvaluje": user.nadriadeny
          },
        }
      };
    }
  }
}

