steps.script:
const inputData = inputVisualElement.data;
// zistíme už uložené e-maily
let currentEmails = '';
if (inputData && inputData.data && inputData.data.fakturacneEMaily) {
    currentEmails = inputData.data.fakturacneEMaily;
}

// e-mail z poľa „novyEmail“
let newEmail = '';
if (inputData && inputData.novyEmail) {
    newEmail = inputData.novyEmail.trim();
}

// rozdelíme reťazec na pole, aby sa s ním lepšie pracovalo
let emailArray = [];
if (currentEmails) {
    emailArray = currentEmails.split(',').map(function (item) {
        return item.trim();
    });
}

// ak nový e-mail ešte v poli nie je, pridáme ho
if (newEmail && emailArray.indexOf(newEmail) === -1) {
    emailArray.push(newEmail);
}

// poskladáme späť do reťazca
const noveEmaily = emailArray.join(', ');

return {
    command: 'query',
    payload: {
        'documentTypeId': '67517022872c0cd4254dd9ae',
        'data._id': '682aeb8a6cb9253777a1fee3',
        'data.fakturacneEMaily': noveEmaily
    }
};

