data.script:
const { inputDocument } = input

return {
    command: "query",
    payload: {
        documentTypeId: '680b545c6dd3fe0299c545bb', // Plán FA - Projekty
        'data.projekt._name': inputDocument.data._name
    }
};

const { inputDocument } = input
const planFa = input.previousCommandOutput;
log(planFa.length)

const planovanaFa = planFa
    .filter(item => item.data.stav === 'planovane' ||
        item.data.stav === 'vykazat' ||
        item.data.stav === 'vykazane' ||
        item.data.stav === 'vyfakturovat' ||
        item.data.stav === 'vyfakturovatZaloha')
    .reduce((a, b) => a + b.data.sumar.spoluBezDphVEur, 0)
const vyfakturovaneFa = planFa
    .filter(item => item.data.stav === 'vyfakturovane' ||
        item.data.stav === 'vyfakturovaneZaloha')
    .reduce((a, b) => a + b.data.sumar.spoluBezDphVEur, 0)
const uhradeneFa = planFa
    .filter(item => item.data.stav === 'zapocitane' ||
        item.data.stav === 'uhradene' ||
        item.data.stav === 'ciastocneUhradene' ||
        item.data.stav === 'preuhradene')
    .reduce((a, b) => a + b.data.sumar.spoluBezDphVEur, 0)

const ocakavanyZisk = (planovanaFa + vyfakturovaneFa + uhradeneFa) * (inputDocument.data.ocakavanaMarza / 100);
const realnyZisk = uhradeneFa - (inputDocument.data.nakladyVEurBezDph ?? 0)
log("Projekt: " + inputDocument.data._name + " Plánované: " + planovanaFa + "€, Vyfakturované: " + vyfakturovaneFa + "€, Uhradené: " + uhradeneFa + "€, Očakávaný zisk: " + ocakavanyZisk + "€, Reálny zisk: " + realnyZisk + "€")

return {
    command: "updateOne",
    payload: {
        filter: {
            documentTypeId: inputDocument.documentTypeId,
            _id: inputDocument._id
        },
        update: {
            "data.planovaneVEurBezDph": planovanaFa,
            "data.vyfakturovaneVEurBezDph": vyfakturovaneFa,
            "data.uhradeneVEurBezDph": uhradeneFa,
            "data.ocakavanyZiskVEurBezDph1": ocakavanyZisk,
            "data.ziskVEurBezDph": realnyZisk,
        },
    }
};

