data.script:
const { inputDocument } = input
const rec = inputDocument.data

return {
  command: "query",
  payload: {
    documentTypeId: '674af84b390db671217ceab6',
    'data.email': rec.zadavatel
  },
};

const { inputDocument } = input
const user = input.previousCommandOutput[0];
const rec = inputDocument.data

const documentTypeId = "685bd77f1953943be2320f14"
const notifikacia = `<p>${input.inputData.user.email} ti posiela notifikáciu k Úlohe: <a href="${rec.linkUlohy} target='_blank'">#${rec._id}</a> ktorá je v stave: ${rec.stav}</p>`

const date = new Date()
const timestamp = date.getTime()

const taskData = {
    notifikaciaK: 'uloha',
    notifikacia: notifikacia,
    stav: 'neprecitana',
    datumACasVytvorenia: timestamp,
    urcenePre: {
        _uid: user._id,
        email: user.data.email,
        _name: user.data._name
    }
}
log(taskData)
return {
    command: "createOne",
    payload: {
        documentTypeId,
        data: taskData
    },
};

const queryObject = { c: 'detail', documentId: input.inputDocument._id };
const encoded = encodeURIComponent(JSON.stringify(queryObject));

const linkUlohy =
    `https://modulario.cloud/companies/${input.inputDocument.companyId}` +
    `/_documents/${input.inputDocument.documentTypeId}` +
    `?1=${encoded}&visual=form&tab=detail`;
return {
    command: 'updateOne',
    payload: {
        filter: { _id: input.inputDocument._id },
        update: {
            'data.linkUlohy': linkUlohy
        }
    }
};

const record = input.inputDocument.data;
const userEmail = input.inputData.user.email;
const klienti = record?.projekt?.klienti ?? [];

const matchFound = Array.isArray(klienti)
    ? klienti.some(k => k.email === userEmail)
    : Object.values(klienti).some(k => k.email === userEmail);

// log(matchFound)
// log(userEmail)
// log(klienti)

if (matchFound) {
    return {
        command: "select",
        output: {},
    };
}

return {
    command: 'noop'
}

const diskusia = input.inputDocument.data?.diskusia;
if (diskusia?.length !== 0) {
  const snapshotDiskusia = input.inputDocument.snapshotData?.diskusia;
  if (!input.inputDocument.snapshotData || input.inputDocument.snapshotData.length === 0) {
    return {
      command: "select"
    };
  }
  if (snapshotDiskusia.length === diskusia?.length
    || (!snapshotDiskusia && diskusia?.length !== 0)) {
    return;
  }
  return {
    command: "select"
  };
}

const { inputDocument } = input
const rec = inputDocument.data

const emails = rec.diskusia.map(item => item.adresat.email)

return {
  command: "query",
  payload: {
    documentTypeId: '674af84b390db671217ceab6',
    'data.email': { $in: emails }
  },
};

const { inputDocument } = input;
const users = Array.isArray(input.previousCommandOutput)
    ? input.previousCommandOutput
    : [];

const rec = inputDocument.data;

const documentTypeId = "685bd77f1953943be2320f14";

// Build per‑user notification objects
const commands = users.map((user) => {
    const notifikaciaHTML = `<p>Máš novú správu v diskusii pri úlohe: 
      <a href="${rec.linkUlohy}" target="_blank">#${rec._id}</a>, 
      ktorá je v stave: ${rec.stav}</p>`;

    return {
        notifikaciaK: "uloha",
        notifikacia: notifikaciaHTML,
        typNotifikacie: "ulohyDiskusia",
        stav: "neprecitana",
        datumACasVytvorenia: Date.now(),
        urcenePre: {
            _uid: user._id,
            email: user.data.email,
            _name: user.data._name,
        },
        uloha: {
            _uid: inputDocument._id,
            _id: rec._id,
            _name: rec._name,
        },
    };
});

// log(commands);

return {
    command: "createMany",
    payload: {
        documentTypeId,
        data: commands,
    },
};

return {
    command: "query",
    payload: {
        documentTypeId: "674af84b390db671217ceab6",
        'data.email': input.inputData.user.email
    },
};

const users = input?.previousCommandOutput || [];
if (users.length === 0) {
    return;
}

const user = users[0];

const DEFAULT_PROJECT = {
    _name: 'AMCEF',
    _uid: '67751cbc170f4baf5754aa4f'
};

const projekt   = { ...(input.inputDocument.data.projekt || {}) }; // shallow-clone existing
const preferred = user.data.predvolenyProjekt || {};

projekt._name = projekt._name || preferred._name || DEFAULT_PROJECT._name;
projekt._uid  = projekt._uid  || preferred._uid  || DEFAULT_PROJECT._uid;

projekt.klienti = projekt.klienti || preferred.klienti || [];
projekt.pracovnici = projekt.pracovnici || preferred.pracovnici || [];

return {
    command: 'updateOne',
    payload: {
        filter: { _id: input.inputDocument._id },
        update: {
            $set: { 'data.projekt': projekt }
        }
    }
};

const { inputDocument } = input
const rec = inputDocument.data

if (rec.zodpovednaOsoba) {
  return {
    command: "query",
    payload: {
      documentTypeId: '674af84b390db671217ceab6',
      'data.email': rec.zodpovednaOsoba.email
    },
  };
} else {
  return { command: 'noop' }
}

const { inputDocument } = input;
const user = input.previousCommandOutput[0];     // result of the query in script 1
const rec = inputDocument.data;                 // current task data

const currZO = rec?.zodpovednaOsoba || null;     // current value (may be null/undefined)

/*  snapshotData can be shaped either as the whole “data” object or as
    data.* directly.  Handle both safely.                                   */
const snap = inputDocument.snapshotData;
const prevZO = snap
    ? (snap.data ? snap.data.zodpovednaOsoba : snap.zodpovednaOsoba) || null
    : null;

let shouldCreate = false;

if (!snap) {                               // task is being saved for the first time
    shouldCreate = !!currZO;                  // create only if a responsible person exists
} else {                                    // task already existed before
    if (currZO && prevZO) {
        // both exist – create only if they differ
        shouldCreate = currZO.email !== prevZO.email;
    } else if (currZO && !prevZO) {
        // new value set where none existed before
        shouldCreate = true;
    } else {
        // every other combination → noop
        shouldCreate = false;
    }
}

if (!shouldCreate) {
    return { command: 'noop' };
}

/* ----- build the notification document ----- */
const documentTypeId = '685bd77f1953943be2320f14';

const notifikacia = `
  <p>Boli ste nastavený ako zodpovedná osoba na úlohe 
     <a href="${rec.linkUlohy}" target="_blank">#${rec._id}</a>, 
     ktorá je v stave: ${rec.stav}</p>`.trim();

const taskData = {
    notifikaciaK: 'uloha',
    notifikacia,
    typNotifikacie: 'ulohyZmenaZodpovednejOsoby',
    stav: 'neprecitana',
    datumACasVytvorenia: Date.now(),
    urcenePre: {
        _uid: user._id,
        email: user.data.email,
        _name: user.data._name,
    },
    uloha: {
        _uid: inputDocument._id,
        _id: rec._id,
        _name: rec._name,
    },
};

return {
    command: 'createOne',
    payload: {
        documentTypeId,
        data: taskData,
    },
};

const record = input.inputDocument.data;
if (record.diskusia.length === 0) {
    return
}
const diskusia = record?.diskusia;
// fill in any missing autor
for (let i = 0; i < diskusia?.length; i++) {
    if (!diskusia[i].autor || diskusia[i].autor.trim() === "") {
        diskusia[i].autor = input.inputData.user.email;
        diskusia[i].autorBolVyplneny = true;
    } else {
        diskusia[i].autor = diskusia[i].autor;
        diskusia[i].autorBolVyplneny = true;
    }
}

// now push the updated array back to the document
return {
    command: 'updateOne',
    payload: {
        filter: { _id: input.inputDocument._id },
        update: {
            'data.diskusia': diskusia
        }
    }
};

/* ─── constants ────────────────────────────────────────────────────────── */
const ZONE = 'Europe/Bratislava';
/** Returns an object {year, month, day, hour, minute, second, ms}
 *  for the instant `epochMs` *as it looks in ZONE*.
 */
function partsInZone(epochMs) {
  const fmt = new Intl.DateTimeFormat('en-GB', {
    timeZone: ZONE,
    hour12: false,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    fractionalSecondDigits: 3,
  });

  return fmt.formatToParts(new Date(epochMs))
    .reduce((o, p) => {
      if (p.type !== 'literal') o[p.type] = +p.value;
      return o;
    }, {});             // e.g. { year: 2025, month: 5, day: 1, hour: 0, … }
}

/** Offset (in ms) between UTC and ZONE **at the instant `dUTC`**. */
function zoneOffsetMs(dUTC) {
  const p = partsInZone(dUTC.getTime());

  // Re-create that same wall-clock time as UTC → its epoch.
  const utcOfWallClock = Date.UTC(
    p.year, p.month - 1, p.day, p.hour, p.minute, p.second,
    p.fractionalSecond || 0,
  );

  // utcOfWallClock - realUTC = how far ZONE is *ahead* of UTC (positive eastward)
  return utcOfWallClock - dUTC.getTime();      // +3 600 000 or +7 200 000
}

/** Epoch ms of 00:00:00.000 (local) on the 1st of `year`-`month` in ZONE. */
function startOfMonthInZone(year, month /* 1-12 */) {
  // Guess: 1st 00:00 as if UTC, then pull it back by the offset that applies *there*.
