/* ─── constants ────────────────────────────────────────────────────────── */
const ZONE = 'Europe/Bratislava';
/** Returns an object {year, month, day, hour, minute, second, ms}
 *  for the instant `epochMs` *as it looks in ZONE*.
 */
function partsInZone(epochMs) {
    const fmt = new Intl.DateTimeFormat('en-GB', {
        timeZone: ZONE,
        hour12: false,
        year: 'numeric', month: '2-digit', day: '2-digit',
        hour: '2-digit', minute: '2-digit', second: '2-digit',
        fractionalSecondDigits: 3,
    });

    return fmt.formatToParts(new Date(epochMs))
        .reduce((o, p) => {
            if (p.type !== 'literal') o[p.type] = +p.value;
            return o;
        }, {});             // e.g. { year: 2025, month: 5, day: 1, hour: 0, … }
}

/** Offset (in ms) between UTC and ZONE **at the instant `dUTC`**. */
function zoneOffsetMs(dUTC) {
    const p = partsInZone(dUTC.getTime());

    // Re-create that same wall-clock time as UTC → its epoch.
    const utcOfWallClock = Date.UTC(
        p.year, p.month - 1, p.day, p.hour, p.minute, p.second,
        p.fractionalSecond || 0,
    );

    // utcOfWallClock - realUTC = how far ZONE is *ahead* of UTC (positive eastward)
    return utcOfWallClock - dUTC.getTime();      // +3 600 000 or +7 200 000
}

/** Epoch ms of 00:00:00.000 (local) on the 1st of `year`-`month` in ZONE. */
function startOfMonthInZone(year, month /* 1-12 */) {
    // Guess: 1st 00:00 as if UTC, then pull it back by the offset that applies *there*.
    const guessUTC = new Date(Date.UTC(year, month - 1, 1));
    const offset = zoneOffsetMs(guessUTC);
    return guessUTC.getTime() - offset;
}

/* ─── build last-month window ─────────────────────────────────────────── */

const nowUTC = new Date();                       // when the worker runs
const { year: thisY, month: thisM } = partsInZone(nowUTC.getTime());

const startOfThisMonth = startOfMonthInZone(thisY, thisM);
const lastMonth = thisM === 1 ? 12 : thisM - 1;
const lastYear = thisM === 1 ? thisY - 1 : thisY;
const startOfLastMonth = startOfMonthInZone(lastYear, lastMonth);
const endOfLastMonth = startOfThisMonth - 1;   // 23:59:59.999 local of last month

const zaciatok = startOfLastMonth;   // 1 May 2025 00:00:00.000 CEST  → 174 605 040 000
const koniec = endOfLastMonth;     // 31 May 2025 23:59:59.999 CEST → 174 872 879 999

const { inputDocument } = input;
const record = input.inputDocument.data;

/* ─── query ───────────────────────────────────────────────────────────── */
log(input.inputDocument.data.kolega.email)
return {
    command: 'query',
    payload: {
        documentTypeId: '67a0f4ec2c7b38a9d099bdc1',
        'data.kolega.email': record.kolega.email,
        'data.zaciatok': { $gte: zaciatok, $lte: koniec },
        // 'data.koniec': { $gte: zaciatok, $lte: koniec },
    },
};

/* ─── constants ───────────────────────────────────────────────────────── */
const ZONE = 'Europe/Bratislava';
const LEARN_UID = '67c196c0cbeffdd9825ee219';   // koeficient we treat as “Vzdelávanie”

/* ─── helpers (pure-Intl, no external libs) ───────────────────────────── */

// numeric parts of a moment *in the chosen zone*
function partsInZone(ms) {
    const fmt = new Intl.DateTimeFormat('en-GB', {
        timeZone: ZONE,
        hour12: false,
        year: 'numeric', month: '2-digit', day: '2-digit',
        hour: '2-digit', minute: '2-digit', second: '2-digit',
        fractionalSecondDigits: 3,
    });
    return fmt.formatToParts(new Date(ms)).reduce((o, p) => {
        if (p.type !== 'literal') o[p.type] = +p.value;
        return o;
    }, {});
}

// offset (ms) between UTC and ZONE at moment dUTC
function zoneOffsetMs(dUTC) {
    const p = partsInZone(dUTC.getTime());
    const wallAsUTC = Date.UTC(
        p.year, p.month - 1, p.day, p.hour, p.minute, p.second,
        p.fractionalSecond || 0
    );
    return wallAsUTC - dUTC.getTime();            // +3600000 or +7200000
}

// epoch of 00:00 local on 1st of year-month in ZONE
function startOfMonthInZone(year, month /*1–12*/) {
    const guessUTC = new Date(Date.UTC(year, month - 1, 1));
    return guessUTC.getTime() - zoneOffsetMs(guessUTC);
}

/* ─── build last-month window ─────────────────────────────────────────── */

const nowUTC = new Date();
const { year: y, month: m } = partsInZone(nowUTC.getTime());

const startThis = startOfMonthInZone(y, m);
const prevMonth = m === 1 ? 12 : m - 1;
const prevYear = m === 1 ? y - 1 : y;
const startPrev = startOfMonthInZone(prevYear, prevMonth);
const endPrev = startThis - 1;               // 23:59:59.999 local

const from = startPrev;     // e.g. 174 605 040 000
const to = endPrev;       //      174 872 879 999

/* ─── main work ───────────────────────────────────────────────────────── */

const trackings = input.previousCommandOutput;
const record = input.inputDocument.data;

const trackingArr = Array.isArray(trackings)
    ? trackings
    : Object.values(trackings || {});

log(`total trackings: ${trackingArr.length}`);

const learningTracking = trackingArr.filter(
    t => t.data?.koeficient?._uid === LEARN_UID
);
const noLearningTracking = trackingArr.filter(
    t => t.data?.koeficient?._uid !== LEARN_UID
);

const learningHoursSum = learningTracking.reduce((s, t) => s + (t.data.trvanie || 0), 0);
const noLearningHoursSum = noLearningTracking.reduce((s, t) => s + (t.data.trvanie || 0), 0);

log(`learning hrs: ${learningHoursSum}`);
log(`non-learning hrs: ${noLearningHoursSum}`);

const learningLimit = +record.vzdelavanieLimit || 0;
const learningToLimit = Math.min(learningHoursSum, learningLimit);

const trackingRecords = trackings.map(t => {
    const d = t.data || {};
    return {
        _uid: t._id,           // whichever side has it
        kolegaStav: d.kolegaStav,
        _id: d._id,
        kolega: d.kolega,
        trvanie: d.trvanie,
        ...(d.koeficient && { koeficient: d.koeficient }),
    };
});

return {
    command: 'updateOne',
    payload: {
        filter: {
            _id: input.inputDocument._id,
            'data._id': input.inputDocument.data._id,
            'data.predpokladanyDatumDodania': { $gte: from, $lte: to },
        },
        update: {
            'data.realneHodiny': noLearningHoursSum,
            'data.vzdelavanie': learningToLimit,
            'data.trackovanie': trackingRecords
        },
        recalculateEquations: true,
    },
};

