data.script:
const { inputDocument } = input;

/* 1 ── partition the activities */
const doneActivities = inputDocument.data.salesAktivity
  .filter(a => a.stav === 'done');

const toDoActivities = inputDocument.data.salesAktivity
  .filter(a => a.stav === 'toDo');

/* 2 ── pick the oldest item in an array, or null */
const pickOldest = arr =>
  arr.length
    ? arr.reduce((prev, curr) =>
      new Date(prev.datum) < new Date(curr.datum) ? prev : curr
    )
    : null;
const pickNewest = arr =>
  arr.length
    ? arr.reduce((prev, curr) =>
      new Date(prev.datum) > new Date(curr.datum) ? prev : curr
    )
    : null;

/* 3 ── pick candidates (may be null) */
const oldestDoneActivity = pickNewest(doneActivities);
const oldestToDoActivity = pickOldest(toDoActivities);

/* 4 ── date helper: DD.MM.YYYY */
function formatDateISO(dateStr) {
  if (!dateStr) return '';                    // empty → keep it empty
  const d = new Date(dateStr);
  const dd = String(d.getDate()).padStart(2, '0');
  const mm = String(d.getMonth() + 1).padStart(2, '0');  // zero-based
  const yyyy = d.getFullYear();
  return `${dd}.${mm}.${yyyy}`;
}

/* 5 ── build a snapshot or an empty object with '' values */
const buildSnapshot = a => a
  ? {
    datumAktivity: a.datum,
    aktivita: a.aktivita,
    link: {
      _uid: a._uid,
      _id: a._id,
      aktivita: a.aktivita.replace(/<\/?p>/g, ' ').trim(),
      datumAktivity: a.datum,
      stav: a.stav,
    },
  }
  : {
    datumAktivity: 0,
    aktivita: '',
    // link: { _uid: '', _id: '', aktivita: '', datum: '', stav: '' },
    link: {},
  };

/* 6 ── compose the updateOne command */
return {
  command: 'updateOne',
  payload: {
    filter: { _id: inputDocument._id },
    update: {
      'data.poslednaZrealizovanaAktivita': buildSnapshot(oldestDoneActivity),
      'data.nasledujucaAktivita': buildSnapshot(oldestToDoActivity),
    },
  },
};

const diskusia = input.inputDocument.data.diskusia;

// fill in any missing autor
for (let i = 0; i < diskusia.length; i++) {
    if (!diskusia[i].autor || diskusia[i].autor.trim() === "") {
        diskusia[i].autor = input.inputData.user.email;
    } else {
        diskusia[i].autor = diskusia[i].autor;
    }
}

// now push the updated array back to the document
return {
    command: 'updateOne',
    payload: {
        filter: { _id: input.inputDocument._id },
        update: {
            'data.diskusia': diskusia
        }
    }
};

const statuses = ['reported', 'backlog', 'blocked', 'toDo'];

const totalOdhad = input.inputDocument.data.ulohy
    .filter(u => statuses.includes(u.stav))
    .map(u => Number(u.odhad ?? 0))
    .reduce((sum, odhad) => sum + odhad, 0);


return {
    command: 'updateOne',
    payload: {
        filter: { _id: input.inputDocument._id },
        update: {
            'data.odhadovanyCas': totalOdhad
        }
    }
};

