const { inputDocument } = input;
const data = inputDocument.data;
const user = input.inputData.user;

const zoznam = data.oboznamiliSa || [];
const meno = user._name;
const now = Date.now();

// Try to find existing entry by meno
const existingEntry = zoznam.find(entry => entry.meno === meno);

if (existingEntry) {
  // Update the existing entry's date
  existingEntry.datum = now;
} else {
  // Add a new entry with generated _id
  zoznam.push({
    _id: `id-${now}-${Math.floor(Math.random() * 1000)}`,  // simple ID generator
    meno: meno,
    datum: now
  });
}
let nameArray = zoznam.map(item => item.meno)
let oboznamiliSaText = nameArray.join(', ')
return {
  command: "updateOne",
  payload: {
