data.script:
// Input document
const document = input.inputDocument;
const documentData = document.data;

// Ensure polozky exists in the document
if (!documentData.polozky) {
   return { command: 'noop' };
}

// Check if ZFA exists
if (!documentData.zalohovaFaktura) {
   return { command: 'noop' };
}

// Extract DD k PP from ZFA
const zfaData = documentData.zalohovaFaktura;
if (!zfaData.danovyDoklad || !Array.isArray(zfaData.danovyDoklad) || zfaData.danovyDoklad.length === 0) {
    return { command: 'noop' };
}

// Extract amounts from the first linked DD k PP inside ZFA
const ddKppData = zfaData.danovyDoklad[0];
const baseVat = ddKppData.sumar?.zakladDph || 0;
const vatAmount = ddKppData.sumar?.vyskaDph || 0;
const totalAmount = baseVat + vatAmount;

// Log extracted values
log(`DD k PP Detected inside ZFA: Základ DPH=${baseVat}, Výška DPH=${vatAmount}, Total=${totalAmount}`);

// Prepare new item with negative values for Polozky
const newPolozka = {
    "polozka": `Odpočet uhradené zálohy ${zfaData._id}`,
    "pocet": 1,
    "mj": { "mernaJednotka": "ks" },
    "dph": 23,
    "cenaBezDph": -Math.abs(baseVat),
    "cenaSDph": -Math.abs(totalAmount),
    "spoluSDph": `${-Math.abs(totalAmount)} ${documentData.mena._name}`,
    "spoluSDphPoZlave": -Math.abs(totalAmount),
};

// Update polozky with new negative entry
const updatedPolozky = [...documentData.polozky, newPolozka];

// Update the summary (sumar) by subtracting the DD k PP values
const updatedSumar = {
    "vyskaDph": (documentData.sumar?.vyskaDph || 0) - Math.abs(vatAmount)
};

// Ensure Zostava Uhradit reflects the correct balance
//const updatedZostavaUhradit = (documentData.zostavaUhradit || 0) - Math.abs(totalAmount);

// Log updates
log(`Updated Polozky: ${JSON.stringify(updatedPolozky, null, 2)}`);
log(`Updated Sumar: ${JSON.stringify(updatedSumar, null, 2)}`);

// Apply the updates
return {
    command: "updateOne",
    payload: {
        filter: { "_id": document._id },
        update: {
            "data.polozky": updatedPolozky,
            "data.sumar": updatedSumar
        }
    }
};

// Fetch exchange rate document (kurzovní lístek) based on document date and currency
return {
    command: 'query',
    payload: {
        documentTypeId: '67a31e9f9ab52c75baeb09d2',
        'data.datum': input.inputDocument.data.datumVystavenia,
        'data.vstupnaMena': input.inputDocument.data.mena._name,
        'data.vystupnaMena': 'EUR'
    }
};

// Process fetched exchange rate
const exchangeRates = input.previousCommandOutput;
const document = input.inputDocument.data;

// Check if an exchange rate exists for the given date and currency
if (exchangeRates.length === 0) {
    throw new Error(`No exchange rate found for ${document.mena._name} on ${document.datum}.`);
}

const exchangeRate = exchangeRates[0]; // Assume one exchange rate per currency and date

// Update document with exchange rate
return {
    command: 'updateOne',
    payload: {
        filter: { 'data._id': document._id },
        update: { $set: { 'data.kurzTest': exchangeRate.data.kurz } }
    }
};

