const payload = {
    "documentTypeId": "678a23a69d5242ca2f11a17e", 
    "data._id": { $regex: `^DD${new Date().getFullYear()}` },
    "$limit": 1,
    "$sort": { "data._id": -1 }
};

log("Query Payload:", JSON.stringify(payload, null, 2));

return {
  command: "query",
  payload: payload
};

// Input document
const document = input.inputDocument;
const documentData = document.data;

// Get the current year in YYYY format
const currentYear = new Date().getFullYear().toString();

// Fetch the last document with the highest sequential number (if available)
const lastDocument = input.previousCommandOutput?.[0] || null;
if(!lastDocument){
throw new Error (lastDocument)
}
let lastSequentialNumber = 0;

// Extract the highest sequence number from the last document
if (lastDocument) {
    const match = lastDocument.data._id.match(/^DD(\d{4})(\d{4})$/); // Match "DDYYYYNNNN"
    if (match) {
        const year = match[1];
        const number = parseInt(match[2], 10);
        
        if (year === currentYear) {
            lastSequentialNumber = number; // Take the last sequence number
        }
    }
}

// Increment the sequence number
const newSequentialNumber = (lastSequentialNumber + 1).toString().padStart(4, "0");

// Generate the new document ID in "DDYYYYNNNN" format
const newDocumentId = `DD${currentYear}${newSequentialNumber}`;

// Update the document with the new ID
return {
  command: "updateOne",
  payload: {
    filter: { "_id": document._id }, // Match the current document
    update: {
      "data._id": newDocumentId, // Assign the new document ID
    },
  },
};
